import React from 'react';
import {createStore} from 'redux';


// STORE => global state
export const store = createStore(globalReducer);


// ACTION



// REDUCER
// const allReducers;

const globalReducer = (state = {
    isLoggedIn : false,
    userLoggedIn : null,
}, action) => {
    switch (action.type) {
        case 'login':
            return state.isLoggedIn = true;
    }
}

export default globalReducer;

// DISPATCH
