import React from 'react';
import {createStore} from 'redux';


const initialState = {
    isLoggedIn : false,
    userLoggedIn : null,
}

// REDUCER

const globalReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'LOGIN':
            return {
                ...state,
                isLoggedIn: true,
                userLoggedIn: "user"
            }
        default:
            return state;
    }
}

// STORE => global state
// export const store = createStore(globalReducer);


// ACTION
export const login = () => {
    return {
        type: 'LOGIN'
    }
}




export default globalReducer;

// DISPATCH
