import React from 'react';
import {createStore} from 'redux';



// REDUCER

const globalReducer = (state = {
    isLoggedIn : false,
    userLoggedIn : null,
}, action) => {
    switch (action.type) {
        case 'login':
            return state.isLoggedIn = true;
    }
}

// STORE => global state
export const store = createStore(globalReducer);


// ACTION
export const login = () => {
    return {
        type: 'login',
    }
}




export default globalReducer;

// DISPATCH
